func TestMLDSA87_GetPK(t *testing.T) {
	pk := PKHStrToBin(PK)

	d := newMLDSA87FromSeed(t, HexSeed)
	if !reflect.DeepEqual(pk, d.GetPK()) {
		t.Errorf("PK mismatch\nExpected: %x\nFound: %x", pk, d.GetPK())
	}
}

