func TestWallet_Sign(t *testing.T) {
	for creatorName, creator := range walletCreators {
		for _, tc := range dilithiumWalletTestCases {
			t.Run(fmt.Sprintf("%s_%s", creatorName, tc.name), func(t *testing.T) {
				w := creator(t, tc)
				got, err := w.Sign([]byte(tc.name))
				if err != nil {
					t.Fatal("failed to sign ", err.Error())
				}
				gotStr := hex.EncodeToString(got[:])
				if gotStr != tc.wantSignature {
					t.Errorf("Signature = %v, want %v", gotStr, tc.wantSignature)
				}
			})
		}
	}
}

func TestVerify(t *testing.T) {
	for _, tc := range dilithiumWalletTestCases {
		t.Run(fmt.Sprintf("%s", tc.name), func(t *testing.T) {
			desc := NewDilithiumDescriptor()
			pk, err := HexStrToPK(tc.wantPK)
			if err != nil {
				t.Fatal("failed to decode public key ", err.Error())
			}
			sig, err := hex.DecodeString(tc.wantSignature)
			if err != nil {
				t.Fatal("failed to decode signature ", err.Error())
			}
			if !Verify([]byte(tc.name), sig, &pk, desc.ToDescriptor().ToBytes()) {
				t.Errorf("signature verification failed")
			}
		})
	}
}

func TestWallet_SignAndVerify(t *testing.T) {
	tests := []struct {
		name        string
		message     []uint8
		wantErrSign bool
	}{
		{
			name:        "Sign and verify test message",
			message:     []uint8("test message"),
			wantErrSign: false,
		},
		{
			name:        "Sign and verify empty message",
			message:     []uint8{},
			wantErrSign: false,
		},
		{
			name:        "Sign and verify binary message",
			message:     []uint8{0x1, 0x2, 0x3, 0x4, 0x5},
			wantErrSign: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			// Create a new wallet
			wallet, err := NewWallet()
			if err != nil {
				t.Fatalf("Failed to create wallet: %v", err)
			}

			// Sign the message
			signature, err := wallet.Sign(tt.message)
			if (err != nil) != tt.wantErrSign {
				t.Errorf("Sign() error = %v, wantErr %v", err, tt.wantErrSign)
				return
			}
			if tt.wantErrSign {
				return
			}

			// Verify the signature
			pk := wallet.GetPK()
			desc := wallet.desc.ToDescriptor().ToBytes()

			if !Verify(tt.message, signature[:], &pk, desc) {
				t.Error("Verify() failed for valid signature")
			}

			// Test verification with modified message
			if len(tt.message) > 0 {
				modifiedMsg := make([]uint8, len(tt.message))
				copy(modifiedMsg, tt.message)
				modifiedMsg[0] ^= 0x01 // Modify first byte

				if Verify(modifiedMsg, signature[:], &pk, desc) {
					t.Error("Verify() succeeded for modified message")
				}
			}

			// Test verification with modified signature
			if len(signature) > 0 {
				modifiedSig := make([]uint8, len(signature))
				copy(modifiedSig, signature[:])
				modifiedSig[0] ^= 0x01 // Modify first byte

				if Verify(tt.message, modifiedSig, &pk, desc) {
					t.Error("Verify() succeeded for modified signature")
				}
			}
		})
	}
}

func TestWallet_SignMultiple(t *testing.T) {
	wallet, err := NewWallet()
	if err != nil {
		t.Fatalf("Failed to create wallet: %v", err)
	}

	messages := [][]uint8{
		[]uint8("message 1"),
		[]uint8("message 2"),
		[]uint8("message 3"),
	}

	for i, msg := range messages {
		signature, err := wallet.Sign(msg)
		if err != nil {
			t.Errorf("Failed to sign message %d: %v", i+1, err)
			continue
		}

		pk := wallet.GetPK()
		// Verify each signature
		if !Verify(msg, signature[:], &pk, wallet.desc.ToDescriptor().ToBytes()) {
			t.Errorf("Failed to verify signature for message %d", i+1)
		}
	}
}

// Helper functions that can be used by all tests
type walletCreator func(t *testing.T, tc *dilithiumWalletTestCase) *Wallet

var walletCreators = map[string]walletCreator{
	"FromSeed":         createWalletFromSeed,
	"FromExtendedSeed": createWalletFromExtendedSeed,
	"FromMnemonic":     createWalletFromMnemonic,
}

// Helper functions for creating wallets in different ways
func createWalletFromSeed(t *testing.T, tc *dilithiumWalletTestCase) *Wallet {
	t.Helper()
	extendedSeed, err := common.NewExtendedSeedFromHexString(tc.extendedSeed)
	if err != nil {
		t.Fatalf("failed to create extended seed: %v", err)
	}
	w, err := newWalletFromSeed(extendedSeed.GetSeed())
	if err != nil {
		t.Fatalf("newWalletFromSeed() error = %v", err)
	}
	return w
}

func createWalletFromExtendedSeed(t *testing.T, tc *dilithiumWalletTestCase) *Wallet {
	t.Helper()
	extendedSeed, err := common.NewExtendedSeedFromHexString(tc.extendedSeed)
	if err != nil {
		t.Fatalf("failed to create extended seed: %v", err)
	}
	w, err := NewWalletFromExtendedSeed(extendedSeed)
	if err != nil {
		t.Fatalf("NewWalletFromExtendedSeed() error = %v", err)
	}
	return w
}

func createWalletFromMnemonic(t *testing.T, tc *dilithiumWalletTestCase) *Wallet {
	t.Helper()
	w, err := NewWalletFromMnemonic(tc.wantMnemonic)
	if err != nil {
		t.Fatalf("NewWalletFromMnemonic() error = %v", err)
	}
	return w
}
