func TestNewWallet(t *testing.T) {
	w, err := NewWallet()
	if err != nil {
		t.Error("failed to generate new ml-dsa-87 ", err.Error())
	}
	if w == nil {
		t.Fatal("unexpected nil wallet")
	}
}

func TestWallet_Seed(t *testing.T) {
	for creatorName, creator := range walletCreators {
		for _, tc := range walletTestCases {
			t.Run(fmt.Sprintf("%s_%s", creatorName, tc.name), func(t *testing.T) {
				w := creator(t, tc)
				if got := hex.EncodeToString(w.GetSeed().ToBytes()); got != tc.extendedSeed[descriptor.DescriptorSize*2:] {
					t.Errorf("Seed = %v\nwant %v", got, tc.extendedSeed[descriptor.DescriptorSize*2:])
				}
			})
		}
	}
}

