func TestWallet_SetIndex(t *testing.T) {
	wallet, err := NewWallet(4, xmsscrypto.SHA2_256)
	if err != nil {
		t.Fatalf("Failed to create wallet: %v", err)
	}

	tests := []struct {
		name            string
		setIndex        uint32
		want            uint32 // expected index after setting
		wantErrSetIndex bool
	}{
		{
			name:            "Set initial index to 5",
			setIndex:        5,
			want:            5,
			wantErrSetIndex: false,
		},
		{
			name:            "Attempt to set lower index (should remain at 5)",
			setIndex:        3,
			want:            5,
			wantErrSetIndex: true,
		},
		{
			name:            "Set higher index to 10",
			setIndex:        10,
			want:            10,
			wantErrSetIndex: false,
		},
		{
			name:            "Attempt to set same index (should remain at 10)",
			setIndex:        10,
			want:            10,
			wantErrSetIndex: false,
		},
		{
			name:            "Set to maximum value for height 4",
			setIndex:        15, // 2^4 - 1
			want:            15,
			wantErrSetIndex: false,
		},
		{
			name:            "Attempt to set lower index after max (should remain at 15)",
			setIndex:        12,
			want:            15,
			wantErrSetIndex: true,
		},
		{
			name:            "Attempt to set index after max (should remain at 15)",
			setIndex:        16,
			want:            15,
			wantErrSetIndex: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			err := wallet.SetIndex(tt.setIndex)
			if (err != nil) != tt.wantErrSetIndex {
				t.Errorf("SetIndex() error = %v, wantErr %v", err, tt.wantErrSetIndex)
				return
			}
			if got := wallet.GetIndex(); got != tt.want {
				t.Errorf("After SetIndex(%v), got %v, want %v",
					tt.setIndex, got, tt.want)
			}
		})
	}
}

func TestWallet_Sign(t *testing.T) {
	for creatorName, creator := range walletCreators {
		for _, tc := range xmssWalletTestCases {
			w := creator(t, tc)
			for _, stc := range tc.signingTestCases {
				t.Run(fmt.Sprintf("%s_%s_%s", creatorName, tc.name, stc.name), func(t *testing.T) {
					err := w.SetIndex(stc.signOTSIndex)
					if err != nil {
						t.Fatalf("failed to set index: %v", err)
					}
					got, err := w.Sign(stc.message)
					if (err != nil) != stc.wantErrSign {
						t.Errorf("Sign() error = %v, wantErr %v", err, stc.wantErrSign)
						return
					}
					gotStr := hex.EncodeToString(got[:])
					if gotStr != stc.wantSig {
						t.Errorf("Signature = %v, want %v", gotStr, stc.wantSig)
					}
				})
			}
		}
	}
}

func TestWallet_Verify(t *testing.T) {
	for creatorName, _ := range walletCreators {
		for _, tc := range xmssWalletTestCases {
			for _, stc := range tc.signingTestCases {
				t.Run(fmt.Sprintf("%s_%s_%s", creatorName, tc.name, stc.name), func(t *testing.T) {
					desc, err := NewXMSSDescriptor(tc.wantHashFunc, tc.wantHeight)
					if err != nil {
						t.Fatalf("failed to create xmss descriptor: %v", err)
					}
					pk, err := HexStrToPK(tc.wantPK)
					if err != nil {
						t.Fatal("failed to decode public key ", err.Error())
					}
					sig, err := hex.DecodeString(stc.wantSig)
					if err != nil {
						t.Fatal("failed to decode signature ", err.Error())
					}
					if !Verify(stc.message, sig, &pk, desc.ToDescriptor().ToBytes()) {
						t.Errorf("signature verification failed")
					}
				})
			}
		}
	}
}

