func TestWallet_SignAndVerify(t *testing.T) {
	tests := []struct {
		name        string
		height      xmsscrypto.Height
		hashFunc    xmsscrypto.HashFunction
		message     []uint8
		wantErrSign bool
	}{
		{
			name:        "Sign and verify with SHA2_256",
			height:      4,
			hashFunc:    xmsscrypto.SHA2_256,
			message:     []uint8("test message"),
			wantErrSign: false,
		},
		{
			name:        "Sign and verify with SHAKE_128",
			height:      6,
			hashFunc:    xmsscrypto.SHAKE_128,
			message:     []uint8("another test message"),
			wantErrSign: false,
		},
		{
			name:        "Sign and verify with SHAKE_256",
			height:      8,
			hashFunc:    xmsscrypto.SHAKE_256,
			message:     []uint8("message to sign"),
			wantErrSign: false,
		},
		{
			name:        "Sign and verify empty message",
			height:      4,
			hashFunc:    xmsscrypto.SHA2_256,
			message:     []uint8{},
			wantErrSign: false,
		},
		{
			name:        "Sign and verify binary message",
			height:      4,
			hashFunc:    xmsscrypto.SHA2_256,
			message:     []uint8{0x1, 0x2, 0x3, 0x4, 0x5},
			wantErrSign: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			// Create a new wallet
			wallet, err := NewWallet(tt.height, tt.hashFunc)
			if err != nil {
				t.Fatalf("Failed to create wallet: %v", err)
			}

			// Sign the message
			signature, err := wallet.Sign(tt.message)
			if (err != nil) != tt.wantErrSign {
				t.Errorf("Sign() error = %v, wantErr %v", err, tt.wantErrSign)
				return
			}
			if tt.wantErrSign {
				return
			}

			// Verify the signature
			pk := wallet.GetPK()
			desc := wallet.desc.ToDescriptor().ToBytes()

			if !Verify(tt.message, signature, &pk, desc) {
				t.Error("Verify() failed for valid signature")
			}

			// Test verification with modified message
			if len(tt.message) > 0 {
				modifiedMsg := make([]uint8, len(tt.message))
				copy(modifiedMsg, tt.message)
				modifiedMsg[0] ^= 0x01 // Modify first byte

				if Verify(modifiedMsg, signature, &pk, desc) {
					t.Error("Verify() succeeded for modified message")
				}
			}

			// Test verification with modified signature
			if len(signature) > 0 {
				modifiedSig := make([]uint8, len(signature))
				copy(modifiedSig, signature)
				modifiedSig[0] ^= 0x01 // Modify first byte

				if Verify(tt.message, modifiedSig, &pk, desc) {
					t.Error("Verify() succeeded for modified signature")
				}
			}
		})
	}
}

func TestWallet_SignMultiple(t *testing.T) {
	wallet, err := NewWallet(4, xmsscrypto.SHA2_256)
	if err != nil {
		t.Fatalf("Failed to create wallet: %v", err)
	}

	pk := wallet.GetPK()
	messages := [][]uint8{
		[]uint8("message 1"),
		[]uint8("message 2"),
		[]uint8("message 3"),
	}

	for i, msg := range messages {
		signature, err := wallet.Sign(msg)
		if err != nil {
			t.Errorf("Failed to sign message %d: %v", i+1, err)
			continue
		}

		// Verify each signature
		if !Verify(msg, signature, &pk, wallet.desc.ToDescriptor().ToBytes()) {
			t.Errorf("Failed to verify signature for message %d", i+1)
		}

		// Check that index increased
		if got := wallet.GetIndex(); got != uint32(i+1) {
			t.Errorf("After signing message %d, index = %v, want %v", i+1, got, i+1)
		}
	}
}

func TestWallet_SignAtLastIndex(t *testing.T) {
	// Use height 4 which gives us 16 OTS keys (2^4)
	height := xmsscrypto.Height(4)
	wallet, err := NewWallet(height, xmsscrypto.SHA2_256)
	if err != nil {
		t.Fatalf("Failed to create wallet: %v", err)
	}
	pk := wallet.GetPK()
	// Calculate last valid index (2^height - 1)
	lastIndex := uint32(1<<height - 1)

	// Sign messages until we reach the last index
	for i := uint32(0); i < lastIndex; i++ {
		message := []uint8(fmt.Sprintf("message %d", i))
		signature, err := wallet.Sign(message)
		if err != nil {
			t.Fatalf("Failed to sign message at index %d: %v", i, err)
		}
		if !Verify(message, signature, &pk, wallet.desc.ToDescriptor().ToBytes()) {
			t.Errorf("Failed to verify signature at index %d", i)
		}
	}

	// Now we're at the last index
	if wallet.GetIndex() != lastIndex {
		t.Fatalf("Expected to be at last index %d, but got %d", lastIndex, wallet.GetIndex())
	}

	// Sign with last available OTS key
	lastMessage := []uint8("last possible message")
	lastSignature, err := wallet.Sign(lastMessage)
	if err != nil {
		t.Fatalf("Failed to sign last message: %v", err)
	}
	if !Verify(lastMessage, lastSignature, &pk, wallet.desc.ToDescriptor().ToBytes()) {
		t.Error("Failed to verify signature for last message")
	}

	// Try to sign after using all OTS keys
	message := []uint8("should fail")
	signature, err := wallet.Sign(message)
	if err == nil {
		t.Error("Expected error when signing after last index, but got none")
	}
	if signature != nil {
		t.Error("Expected nil signature when signing after last index")
	}
}

func TestVerify2(t *testing.T) {
	height := xmsscrypto.ToHeight(4)
	w := newTestXMSSWallet(t, height)
	if w == nil {
		t.Errorf("XMSS cannot be nil")
	}

	if w.GetHeight() != height {
		t.Errorf("Height Mismatch\nExpected: %d\nFound: %d", height, w.GetHeight())
	}

	var message [32]uint8

	signature, err := w.Sign(message[:])

	if err != nil {
		t.Errorf("Failed to Sign")
	}

	pk := w.GetPK()
	for i := 0; i < 10; i++ {
		if !Verify(message[:], signature, &pk, w.desc.ToDescriptor().ToBytes()) {
			t.Errorf("Expected True")
		}
	}

	signature[100] = signature[100] + 1
	if Verify(message[:], signature, &pk, w.desc.ToDescriptor().ToBytes()) {
		t.Errorf("Expected False")
	}

	signature[100] = signature[100] - 1
	if !Verify(message[:], signature, &pk, w.desc.ToDescriptor().ToBytes()) {
		t.Errorf("Expected True")
	}

	message[2] = message[2] + 1
	if Verify(message[:], signature, &pk, w.desc.ToDescriptor().ToBytes()) {
		t.Errorf("Expected False")
	}

	message[2] = message[2] - 1
	if !Verify(message[:], signature, &pk, w.desc.ToDescriptor().ToBytes()) {
		t.Errorf("Expected True")
	}
}

