// Helper functions that can be used by all tests
type walletCreator func(t *testing.T, tc *xmssWalletTestCase) *Wallet

var walletCreators = map[string]walletCreator{
	"FromSeed":         createWalletFromSeed,
	"FromExtendedSeed": createWalletFromExtendedSeed,
	"FromMnemonic":     createWalletFromMnemonic,
}

// Helper functions for creating wallets in different ways
func createWalletFromSeed(t *testing.T, tc *xmssWalletTestCase) *Wallet {
	t.Helper()
	extendedSeed, err := common.NewExtendedSeedFromHexString(tc.extendedSeed)
	if err != nil {
		t.Fatalf("failed to create extended seed: %v", err)
	}
	w, err := newWalletFromSeed(extendedSeed.GetSeed(), tc.wantHeight, tc.wantHashFunc)
	if err != nil {
		t.Fatalf("newWalletFromSeed() error = %v", err)
	}
	return w
}

